<script setup lang="ts">  import useToast from "./useToast";  const {items} = useToast();  const mouseOver = (event: Event) => {    event.target?.setAttribute('isHovered', '1');  }  const mouseLeave = (event: Event) => {    event.target?.setAttribute('isHovered', '0');  }</script><template>  <div class="app-toast">    <TransitionGroup        class="app-toast__items"        tag="div"        name="app-toast__items"        mode="out-in"    >      <div        v-for="item in items"        :id="item.id"        :key="item.id"        :class="['app-toast__item', `app-toast__item--${item.type}`]"        @mouseover="mouseOver"        @mouseleave="mouseLeave"        v-html="item.text"      >      </div>    </TransitionGroup>  </div></template><style lang="scss">  .app-toast{    position: fixed;    right: 15px;    top: 15px;    width: fit-content;    height: fit-content;    z-index: 99999;    &__items{      display: flex;      flex-direction: column;      &-enter-active,      &-leave-active {        transition: all 0.5s ease;      }      &-enter-from,      &-leave-to {        opacity: 0;        transform: translateX(30px);      }    }    &__item{      width: 300px;      padding: 8px 15px 6px;      border-radius: 5px;      margin: 8px;      color: #fff;      font-size: 14px;      text-align: center;      display: flex;      align-items: center;      justify-content: center;      backdrop-filter: blur(1.5px);      border: 1px solid transparent;      &--success{        box-shadow: 0 4px 8px 0 rgba(34, 197, 94, 0.04);        background-color: rgba(240, 253, 244, 0.95);        border-color: #bbf7d0;        color: #16a34a;      }      &--error{        box-shadow: 0 4px 8px 0 rgba(239, 68, 68, 0.04);        background-color: rgba(254, 242, 242, 0.95);        border-color: #fecaca;        color: #dc2626;      }      &--warning{        box-shadow: 0 4px 8px 0 rgba(234, 179, 8, 0.04);        background-color: rgba(254, 252, 232, 0.95);        border-color: #fde68a;        border-width: 1px;        color: #ca8a04;      }    }  }</style>